<?php/* * BloonPHP * Habbo R63 Post-Shuffle * Based on the work of Burak, edited by BloonPHP Git Community. * * RaGEZONE @BurakDev *  * https://github.com/BurakDev/BloonPHP */Class Config{	var $file;	var $filename;	public function Init($filename){		if(file_exists($filename)){			$this->file = file_get_contents($filename);			$this->filename = $filename;		}else{			Console::WriteLine("Config file ".$filename." is missing !");			exit;		}	}	public function Get($name){		if($this->file){			$content = $this->file;			$content = str_replace("\r", "", $content);			$content = explode("\n", $content);			foreach($content as $line => $data){				if(preg_match("/".$name."/i", $data)){					$data = str_replace($name."=", "", $data);					$data = str_replace(chr(13), "", $data);					if($data == "1"){						return true;					}else if($data == "0"){						return false;					}else{						return $data;					}					die($name." not found in ". $this->filename ." !");				}			}		}else{			die("\n\nPlease init Config class !\n\n");		}	}}?>